const Countrie = ({countrie}) => {

    if ( countrie.length !== 1){
        return (<></>)
    }

    return(
        <>
            { countrie.map( c => {
                return(
                    <div key={1}>
                        <h3>Countrie info</h3>
                        <div>Official name: {c.name.official} </div>
                        <div>
                            <p>Languages:</p>
                            <ul>
                                { c.languages.map( l => <li>l</li>)}
                            </ul>                            
                        </div>
                        <div><img src={c.flags.png} width="150" height="75"/></div>
                    </div>
                )
                })
            }
        </>
    )
}

export default Countrie
