const Countrie = ({countrie}) => {

    console.log(countrie.length)

    if ( countrie.length !== 1){
        return (<></>)
    }



    return(
        <>
        { countrie.map( c => {
                <div>
                <h3>Countrie info</h3>
                <div>Official name {c.name.official} </div>
                <div>Flag <img src={c.flag} /></div>
                </div>
            })
        }
        </>
    )
}

export default Countrie
