import axios from "axios"
import { useEffect, useState } from "react"

const Weather = ({countrie}) => {
    const [weather, setWeather] = useState([])
    const weatherKey = 'cf750d6bd86d6a0650d0b74b99e57251'
    const hookWeather = () => { 
        axios 
            .get(`https://api.openweathermap.org/data/3.0/onecall?lat=${countrie.latlng[0]}&lon=${countrie.latlng[1]}&exclude=alerts&appid=${weatherKey}`)
            .then( (response) => {
                setWeather(response.data)
            })
            .catch( (e)=>{
                console.log(`Something went wrong ${e}`)
            })
        }

    useEffect(hookWeather, [])

    console.log(weather)


    return(
        <div>
            <h3>Weather in {countrie.name.common}</h3>

        </div>
    )
}

export default Weather
