const Countrie = ({countrie}) => {

    //if ( countrie.length !== 1 ){
    if (countrie.length > 1 && countrie.length < 10){
        return (<>
            <h3>Countries matches</h3>
            <div>
                { countrie.map( c => <div key={c.cioc}>{c.name.common}</div>)}
            </div>
        </>)
    }else if (countrie.length > 10){
        return(<></>)
    }

    return(
        <>
            { countrie.map( c => {
                return(
                    <div key={1}>
                        <h3>Countrie info</h3>
                        <div>Official name: {c.name.official} </div>
                        <div>
                            <p>Languages:</p>
                            <ul>
                                { Object.values(c.languages).map( l => <li key={l}>{l}</li>)}
                            </ul>                            
                        </div>
                        <div><img src={c.flags.png} width="150" height="85"/></div>
                    </div>
                )
                })
            }
        </>
    )
}

export default Countrie
