import { computeHeadingLevel } from '@testing-library/react';
import personService from '../services/person';

const Persons = ({persons, setAlteredData}) => {

    const deletePersonEvent = (id, name) => {
        if (window.confirm(`Are you sure to delete ${name}?`)){        
            personService
                .deletep(id)
                .then(reponse => {
                    alert(`${name} was deleted successfully from the server !`)
                })
                .catch( error => {
                    alert(`Something went wrong \n${error}`)
                    if ( error.request.status === 404){
                        alert(`The ${name} information was deleted previusly`);
                    }
                })
        }
    }

    return(
        <div>
            <h3>Contacts</h3>
            { persons.map( p => <div key={p.id} >{p.name}, {p.number} <button onClick={() => deletePersonEvent(p.id,p.name)}>delete</button></div> ) } 
        </div>
    )    
}

export default Persons
