const Persons = ({persons}) => {

    const deletePerson = (id, name) => {
        const resp = confirm(`Are you sure to delete ${name}`)
        console.log(resp);
    }

    return(
        <div>
            <h3>Contacts</h3>
            { persons.map( p => <div key={p.id} >{p.name}, {p.number} <button onAuxClick={() => deletePerson(p.id,p.name)}>delete</button></div> ) } 
        </div>
    )    
}

export default Persons
