            }
        }else {

            personService
            .create(person)
            .then( response => {
                alert(`The contact was created successfully`)
                console.log(response);
                setPersons(persons.concat(person))
            }).catch( error => {
                alert(`Something went wrong when we treid to update data on the server.\n${error}\n\n${error.request.response}`)
            })
        }

    }

